CREATE TABLE IF NOT EXISTS "__EFMigrationsHistory" (
    "MigrationId" character varying(150) NOT NULL,
    "ProductVersion" character varying(32) NOT NULL,
    CONSTRAINT "PK___EFMigrationsHistory" PRIMARY KEY ("MigrationId")
);

CREATE TABLE "Baskets" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "CustomerId" integer NOT NULL,
    CONSTRAINT "PK_Baskets" PRIMARY KEY ("Id")
);

CREATE TABLE "BasketItems" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "CustomerBasketId" integer NOT NULL,
    "ProductId" integer NOT NULL,
    "ProductName" text NULL,
    "ProductPrice" double precision NOT NULL,
    "Count" integer NOT NULL,
    CONSTRAINT "PK_BasketItems" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_BasketItems_Baskets_CustomerBasketId" FOREIGN KEY ("CustomerBasketId") REFERENCES "Baskets" ("Id") ON DELETE CASCADE
);

CREATE INDEX "IX_BasketItems_CustomerBasketId" ON "BasketItems" ("CustomerBasketId");

